package baekjoon_java.P17478;import java.util.Scanner;public class Main {    private static final String s1 = "\"재귀함수가 뭔가요?\"";    private static final String s2 = "\"잘 들어보게. 옛날옛날 한 산 꼭대기에 이세상 모든 지식을 통달한 선인이 있었어.";    private static final String s3 = "마을 사람들은 모두 그 선인에게 수많은 질문을 했고, 모두 지혜롭게 대답해 주었지.";    private static final String s4 = "그의 답은 대부분 옳았다고 하네. 그런데 어느 날, 그 선인에게 한 선비가 찾아와서 물었어.\"";    private static final String s5 = "라고 답변하였지.";    private static final String finish = "\"재귀함수는 자기 자신을 호출하는 함수라네\"";    private static final String depth = "____";    static int n;    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        n = sc.nextInt();        System.out.println("어느 한 컴퓨터공학과 학생이 유명한 교수님을 찾아가 물었다.");        print(n);    }    private static void print(int m) {        String dep = depth.repeat(n - m);        System.out.println(dep + s1);        if (m == 0) {            System.out.println(dep + finish);            System.out.println(dep + s5);            return;        }        System.out.println(dep + s2);        System.out.println(dep + s3);        System.out.println(dep + s4);        print(m - 1);        System.out.println(dep + s5);    }}